'use client'
import {useState} from "react"
import {doc , collection, onSnapshot, getDocs} from "firebase/firestore"
import  {db}  from "../../store/firebase";
import {Card} from 'antd'
const Products = () =>{
    const [productList, setProductList] = useState([]);

    const productCollectionRef = collection(db,"products");

    const getProductList = async () =>{
        try{
            const data  = await getDocs(productCollectionRef);
            const filteredData : any = data.docs.map((doc) => ({
                ...doc.data(),
                id: doc.id,
              }));
              setProductList(filteredData);
              console.log(filteredData);
        }catch(err){
            console.error(err);
        }
    }
    return( <>
        <button onClick={getProductList}>getdata</button>
        <div className="py-8 px-[5vw] grid grid-cols-4">
  {productList.map((product: any) => (
    <Card key={product.id} style={{ width: '21vw', margin: 10 }}>
      <div className="flex flex-col items-center">
        <div className="flex flex-col h-60">
          <img className="rounded-md h-44" src={product.imageURL || 'data:image/jpeg;base64,'} />
          <div className="mt-5 mb-6 text-center">{product.name}</div>
        </div>
        <div className="font-bold text-center">{product.price}</div>
      </div>
    </Card>
  ))}
</div>

      </>
    )
}

export default Products;


